package assignment.week1.controlstatements;

public class WhileAndDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//Program with while
		int i=10;
		while(i>=1)
		{
			System.out.println(i);
			i--;
		}
		
		//Program with do while
		int j=10;
		do {
			System.out.println(j);
			j--;
		}
		while(j>=1);
	}

}
