package assignments.oops.polymorphism;

public class ParentClassOverRiding {
	 
	static float price=250.0f;
	public static void stockprice()
	{
		float price1 = price;
		System.out.println("The price is "+ price1);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		stockprice();

	}

}
